import{_ as l,o as i,c as a,Q as e}from"./chunks/framework.fb3766a7.js";const m=JSON.parse('{"title":"如何写简历","description":"","frontmatter":{},"headers":[],"relativePath":"interview/第三章/3-7如何写简历.md","lastUpdated":1707358331000}'),t={name:"interview/第三章/3-7如何写简历.md"},o=e('<h1 id="如何写简历" tabindex="-1">如何写简历 <a class="header-anchor" href="#如何写简历" aria-label="Permalink to &quot;如何写简历&quot;">​</a></h1><h3 id="划重点" tabindex="-1">划重点 <a class="header-anchor" href="#划重点" aria-label="Permalink to &quot;划重点&quot;">​</a></h3><ul><li>简历就像高考作文-阅卷时间非常短</li><li>内容简洁</li><li>直击重点，表现出自己的优势</li></ul><blockquote><p>优势：自己熟练掌握的、能满足用人单位的需求</p></blockquote><h3 id="简历包含的内容" tabindex="-1">简历包含的内容 <a class="header-anchor" href="#简历包含的内容" aria-label="Permalink to &quot;简历包含的内容&quot;">​</a></h3><ul><li>个人信息 <ul><li>必备： 姓名、性别、电话、邮箱、籍贯</li><li>年龄可以不写</li><li>头像无所谓</li></ul></li><li>教育经历 <ul><li>写上最高学历</li></ul></li><li>专业技能 <ul><li>表现自己的核心竞争力</li><li>内容不要太多， 3 ～ 5 条</li><li>太基础的不要写</li><li>条理清晰</li></ul></li><li>工作经历 <ul><li>如实写</li><li>写明公司，职位、入职、离职时间</li><li>如果有空窗期，如实写明即可</li></ul></li><li>项目经历 <ul><li>写 2-4 个具有说服力的项目</li><li>项目描述，技术栈、个人角色、解决的项目核心问题、性能优化</li><li>技巧： 可以写别人的项目，只要你能 hold 住</li></ul></li><li>博客和开源</li></ul><h3 id="注意事项" tabindex="-1">注意事项 <a class="header-anchor" href="#注意事项" aria-label="Permalink to &quot;注意事项&quot;">​</a></h3><ul><li>界面不要太花哨，简洁明了即可</li><li>注意用词，“ 精通 熟练 ”等慎用</li><li>不可造假，会被拉入黑名单（项目经历那里，不是造假！！）</li></ul>',8),r=[o];function _(n,u,s,c,d,h){return i(),a("div",null,r)}const f=l(t,[["render",_]]);export{m as __pageData,f as default};
